try:
    from ladybug_{{cad}}.versioning.gather import gather_canvas_components
    from ladybug_{{cad}}.versioning.diff import sync_component
    from ladybug_{{cad}}.{{plugin}} import all_required_inputs, give_warning
except ImportError as e:
    raise ImportError('\nFailed to import ladybug_{{cad}}:\n\t{}'.format(e))


if all_required_inputs(ghenv.Component) and _sync:
    # load all of the {{PLGN}}Python userobjects and update the versions
    components = gather_canvas_components(ghenv.Component)
    report_init = []
    for comp in components:
        try:
            report_init.append(sync_component(comp, ghenv.Component))
        except Exception:
            if hasattr(comp, 'Name'):
                msg = 'Failed to Update "{}"'.format(comp.Name)
                print(msg)
                give_warning(ghenv.Component, msg)
    report = '\n'.join(r for r in report_init if r)
